INSERT INTO `product_details` (`id`, `product_id`, `product_name`, `language_id`, `language_name`, `banner_text`, `short_desc`, `full_desc`, `meta_title`, `meta_desc`, `meta_keys`, `created_at`, `updated_at`) VALUES
(1, 1, 'Super Green Hermetic Bags', 1, 'English', NULL, 'Our super Green Hermetic Bags are an intertwined combination of a primary bag (a 9-layered recyclable hermetic food-grade plastic bag) and a secondary bag (high strength eco-friendly).', NULL, 'Super Green Hermetic Bags', 'Super Green Hermetic Bags', 'Super Green Hermetic Bags', '2021-04-17 05:32:18', '2022-02-01 12:21:20'),
(2, 2, 'Ecotact Ship Shield Hermetic Container Liners', 1, 'English', 'Product Details of Ecotact Ship Shield Hermetic Container Liners', 'High-tech Ship Shield Hermetic Container Liner is designed to store and transport grains in a hermetically sealed structure. Manufactured in a cleanroom facility and meeting FDA standards, this ISO 22000:2018, ISO 9001:2015, bags come with high barrier properties which keep the aroma and the moisture content of the grain intact.', NULL, 'Ecotact Ship Shield Hermetic Container Liners | Shipping Grains', 'Ecotact has Ship shield hermetic container liners to store and transport grains, green coffee, and cocoa in sealed packaging with high barrier properties while shipping.', 'Ship Shield Hermetic Container Liners', '2021-04-17 05:49:23', '2022-08-26 14:15:22'),
(3, 3, 'Ecotact Multilayered Hermetic Storage Bags', 1, 'English', 'Product Details of Multilayered Hermetic Storage Bags - Green Coffee & Cacao Sacks', 'The ecological plastic bags inside burlap bags are extremely strong and have high oxygen and moisture barrier properties to seal the freshness of green coffee throughout the supply chain and storage.', NULL, 'Ecotact Multilayered Hermetic Storage Bags | Coffee & Cacao Sacks', 'Ecotact delivers multilayered hermetic storage bags globally. Our green coffee storage bags, grain & cacao sacks maintain quality with high oxygen & moisture barrier.', 'Multilayered Hermetic Storage Bags,  hermetic storage bags,  Cacao sack', '2021-04-17 06:33:36', '2022-08-19 15:38:58'),
(4, 4, 'Ecotact Hermetic FIBC Bags', 1, 'English', 'Product Details of Ecotact Hermetic FIBC storage bags', 'Ecotact Hermetic Flexible Intermediate Bulk Container Bags are high quality polypropylene bags with multilayered high capacity hermetic plastic liners inside for efficient storage and transportation of coffee in bulk.', NULL, 'Buy The Best Ecotact Hermetic FIBC Storage Bags | FIBC Bulk Bags', 'Our FIBC storage Bags have multilayered high-capacity hermetic plastic for efficient storage & transportation of green coffee, cocoa and beans. Order FIBC bulk bags.', 'fibc storage bags, fibc bags', '2021-04-17 06:39:34', '2022-08-18 15:38:31'),
(5, 5, 'Ecotact Sterile Vacuum Bags', 1, 'English', 'Product Details of Ecotact Sterile Vacuum Packaging Bags', 'These high-barrier multi-layered plastic bags are strong &amp; flexible and can preserve food grains for a longer duration. These bags are sealed scientifically after extracting the excess air. They are space convenient and can be easily packed into corrugated cartons wherein branding can be done in the most attractive ways.', NULL, 'Ecotact Vacuum Sealed Coffee Bags | Bolsas Para Empacar Al Vacio', 'Looking for bolsas para envasar sellado al vacio? Ecotact offers Sterile\r\nvacuum packaging bags for grains. Buy our best (sellar al vacio)\r\nvacuum-sealed coffee bags.', 'vacuum sealed coffee bags, vacuum packaging bags, sellado al vacio, bolsas para empacar al vacio, bolsas para envasar al vacio, bolsas para sellar al vacio, bolsa vacuum', '2021-04-17 06:44:01', '2022-06-22 12:20:29'),
(6, 6, 'Ecotact Troiseal Bags', 1, 'English', 'Product Details of Ecotact Troiseal Packaging Bags for Coffee Beans and Other Grains', 'These are tamper-proof hermetically sealed bags designed especially for green coffee &amp; other coffee beans. The flexibility of the bag makes it a perfect match for small and homegrown roasters.', NULL, 'Best Ecotact Troiseal Bags | Buy Reusable Green Coffee Bags', 'Ecotact Troiseal packaging bags are tamper-proof, resealable & reusable green coffee bags especially designed for storing coffee beans, cacao, grains, etc. Shop now!', 'ecotact troiseal bags, reusable coffee bags, Resealable bags, reusable bags', '2021-04-17 06:46:56', '2022-11-22 16:16:15'),
(7, 7, 'Others', 1, 'English', NULL, 'We also deal in Food Grade Jute Bags, Burlap bags, PP bags of customized sizes, shape, quality and capacities. All the raw material used is food grade and USFDA approved.', NULL, 'Others', 'Others', 'Others', '2021-04-17 06:53:12', '2021-05-17 16:20:30'),
(8, 8, 'Ecotact Multilayered Hermetic Sampler Bags', 1, 'English', 'Product Details of Ecotact Multi-layered Hermetic Sampler Packaging Bags', 'Sampling of green coffee by coffee producers/millers/traders/roasters is one of the most critical exercises in the coffee industry.', NULL, 'Ecotact Multilayered Hermetic Sampler Packaging Bags for Coffee', 'Multi-layered Hermetic sampler bags help coffee producers, traders,\r\nmillers and roasters store samples of grains & beans like green coffee\r\nsample bags. Order today.', 'sample coffee bags, coffee sample bags', '2021-04-29 19:03:53', '2022-06-22 12:12:32'),
